<script type="text/javascript">
    $(document).ready(function () {
        $("#price").ionRangeSlider({
            type: "double",
            grid: true,
            min: 0,
            max: 1000,
            from: 200,
            to: 800,
            prefix: "$ ",
            onChange: function (data) {
                $('#price_from').val(data.from);
                $('#price_to').val(data.to);
            }
        });

        $("#area").ionRangeSlider({
            type: "double",
            grid: true,
            min: 0,
            max: 1000,
            from: 200,
            to: 800,
            prefix: "m<sup>2</sup> ",
            onChange: function (data) {
                $('#area_from').val(data.from);
                $('#area_to').val(data.to);
            }
        });
    });
</script>
